var suggestions=document.getElementById('suggestions'),userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(a){a.keyCode===191&&(a.preventDefault(),userinput.focus()),a.keyCode===27&&(userinput.blur(),suggestions.classList.add('d-none'))}document.addEventListener('click',function(a){var b=suggestions.contains(a.target);b||suggestions.classList.add('d-none')}),document.addEventListener('keydown',suggestionFocus);function suggestionFocus(b){const d=suggestions.querySelectorAll('a'),e=[...d],a=e.indexOf(document.activeElement);let c=0;b.keyCode===38?(b.preventDefault(),c=a>0?a-1:0,d[c].focus()):b.keyCode===40&&(b.preventDefault(),c=a+1<e.length?a+1:a,d[c].focus())}(function(){var a=new FlexSearch.Document({tokenize:"forward",cache:100,document:{id:'id',store:["href","title","description"],index:["title","description","content"]}});a.add({id:0,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/general/overview/",title:"Overview",description:".NET WebAPI Boilerplate Template built with .NET 6.0. Incorporates the most essential Packages your projects will ever need. Follows Clean Architecture Principles.",content:'\u003ch2 id="about"\u003eAbout\u003c/h2\u003e\n\u003cp\u003e\u003ccode\u003edotnet-webapi-boilerplate\u003c/code\u003e is the integral part of the \u003ccode\u003efullstackhero\u003c/code\u003e project.\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003efullstackhero\u003c/code\u003e is a venture to develop industry leading boilerplate templates for the dotnet stack as the backend (Web API) along with modern client frameworks like Angular, MVC and Blazor.\u003c/p\u003e\n\u003cp\u003eThis repository contains the WebApi Project of \u003ccode\u003efullstackhero\u003c/code\u003e.\u003c/p\u003e\n\u003ch2 id="release-planning"\u003eRelease Planning\u003c/h2\u003e\n\u003ch3 id="003-rc-is-available-now"\u003e0.0.3 RC is available now!\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eMigration Refresh\u003c/li\u003e\n\u003cli\u003eSecurity Patches\u003c/li\u003e\n\u003cli\u003eImage Upload Fix\u003c/li\u003e\n\u003cli\u003eOption to Disable Swagger\u003c/li\u003e\n\u003cli\u003eCleaner Code\u003c/li\u003e\n\u003cli\u003eActivate / Deactivate Tenants\u003c/li\u003e\n\u003cli\u003eUpgrade Tenant Subscription\u003c/li\u003e\n\u003cli\u003eAdvanced Search\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eThis is the first pre-release version of the \u003ccode\u003efullstackhero .NET WebAPI Boilerplate\u003c/code\u003e package. Newer versions will be available on a weekly basis with newer updates and patches. \u003ca href="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/"\u003eRead the getting-started guide for more.\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eThe Release Version is expected to be out by November 2021 as soon as .NET 6 LTS is launched by Microsoft. Preview versions of this project are available for Initial Developer Testing.\u003c/p\u003e\n\u003ch2 id="quick-start-guide"\u003eQuick Start Guide\u003c/h2\u003e\n\u003cp\u003eOpen up your Command Prompt / Powershell and run the following command to install the solution template.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003edotnet new --install FullStackHero.WebAPI.Boilerplate\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eThis would install the \u003ccode\u003efullstackhero .NET WebAPI Boilerplate\u003c/code\u003e template globally on your machine. With that done, let\u0026rsquo;s see how you can start generating complete .NET WebAPI Solutions seamlessly.\u003c/p\u003e\n\u003cp\u003eSimply navigate to a new directory (wherever you want to place your new solution), and open up Command Prompt at the opened directory.\u003c/p\u003e\n\u003cp\u003eRun the following command. Note that, in this demonstration, I am naming my new solution as \u003ccode\u003eFSH.Starter\u003c/code\u003e.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003edotnet new fsh-api -o FSH.Starter\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eFor further steps and details, \u003ca href="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/"\u003eRead the Getting Started Guide\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id="important-links--documentations"\u003eImportant Links \u0026amp; Documentations\u003c/h2\u003e\n\u003cp\u003eOverview - \u003ca href="https://fullstackhero.net/dotnet-webapi-boilerplate/general/overview/"\u003eRead\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eGetting Started - \u003ca href="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/"\u003eRead\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eDevelopment Environment - \u003ca href="https://fullstackhero.net/dotnet-webapi-boilerplate/general/development-environment/"\u003eLearn about setting up the DEV environment\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eTrack Progress - \u003ca href="https://github.com/fullstackhero/dotnet-webapi-boilerplate/milestone/1"\u003eRelease 1.0 Milestones\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eParticipate in Discussions - \u003ca href="https://github.com/fullstackhero/dotnet-webapi-boilerplate/discussions"\u003eQNA \u0026amp; General Discussions\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eJoin our Discord - \u003ca href="https://discord.gg/gdgHRt4mMw"\u003efullstackhero @ Discord\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id="features"\u003eFeatures\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Built on .NET 6.0\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Follows Clean Architecture Principles\u003c/li\u003e\n\u003cli\u003e\u003cinput disabled="" type="checkbox"\u003e Completely Documented at \u003ca href="https://fullstackhero.net"\u003efullstackhero.net\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Multi Tenancy Support\n\u003cul\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Create Tenants with Multi Database / Shared Database Support\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Activate / Deactivate Tenants on Demand\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Upgrade Subscription of Tenants - Add More Validity Months to each tenant!\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Supports MySQL, MSSQL \u0026amp; PostgreSQL!\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Uses Entity Framework Core as DB Abstraction\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Flexible Repository Pattern\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Dapper Integration for Optimal Performance\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Serilog Integration\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Swagger Support\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Mapster Integration for Quicker Mapping\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e API Versioning\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Response Caching - Distributed Caching\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Fluent Validations\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Audit Logging\u003c/li\u003e\n\u003cli\u003e\u003cinput disabled="" type="checkbox"\u003e Advanced User \u0026amp; Role Based Permission Management\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Code Analysis \u0026amp; StyleCop Integration with Rulesets\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e JSON Based Localization with Caching\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e Hangfire Support\u003c/li\u003e\n\u003cli\u003e\u003cinput checked="" disabled="" type="checkbox"\u003e File Storage Service\u003c/li\u003e\n\u003cli\u003e\u003cinput disabled="" type="checkbox"\u003e Test Projects\u003c/li\u003e\n\u003cli\u003e\u003cinput disabled="" type="checkbox"\u003e \u0026amp; Much More\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="community"\u003eCommunity\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003eDiscord \u003ca href="https://discord.gg/gdgHRt4mMw"\u003e@fullstackhero\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="license"\u003eLicense\u003c/h2\u003e\n\u003cp\u003eThis project is licensed with the \u003ca href="LICENSE"\u003eMIT license\u003c/a\u003e.\u003c/p\u003e\n\u003ch2 id="support-"\u003eSupport â­\u003c/h2\u003e\n\u003cp\u003eHas this Project helped you learn something New? or Helped you at work?\nHere are a few ways by which you can support.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eLeave a star! â­\u003c/li\u003e\n\u003cli\u003eRecommend this awesome project to your colleagues. ðŸ¥‡\u003c/li\u003e\n\u003cli\u003eDo consider endorsing me on LinkedIn for ASP.NET Core - \u003ca href="https://codewithmukesh.com/linkedin"\u003eConnect via LinkedIn\u003c/a\u003e ðŸ¦¸\u003c/li\u003e\n\u003cli\u003eOr, If you want to support this project in the long run, \u003ca href="https://www.buymeacoffee.com/codewithmukesh"\u003econsider buying me a coffee\u003c/a\u003e! â˜•\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003e\u003ca class="btn btn-primary btn-lg px-3 mb-2" style="font-size: large;"\nhref="https://www.buymeacoffee.com/codewithmukesh" target="_blank" role="button"\u003e\u003ci style="margin-right: 5px;" class="fas fa-mug-hot"\u003e \u003c/i\u003e Buy me\na Coffee\u003c/a\u003e\u003c/p\u003e\n'}).add({id:1,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/general/development-environment/",title:"Development Environment",description:"Setting up the Development Environment for the .NET WebApi Boilerplate",content:'\u003cp\u003efullstackhero\u0026rsquo;s \u003cstrong\u003e.NET WebApi Boilerplate\u003c/strong\u003e Project Development needs you to have the following applications / tools available on your machine. Please Note that this project is being built on a Windows 10 Machine using Visual Studio Code IDE.\u003c/p\u003e\n\u003ch2 id="net-sdk"\u003e.NET SDK\u003c/h2\u003e\n\u003cp\u003eAs mentioned earlier, this project is built with the latest available .NET SDK, which is .NET 6.0. Since the SDK is currently at preview, the project will be released by Mid-November as soon as Microsoft launches .NET 6.0 LTS SDK. But that doesn\u0026rsquo;t stop you from testing out the application!\u003c/p\u003e\n\u003cp\u003eEnsure that you have the latest version of the SDK available - \u003ca href="https://dotnet.microsoft.com/download/dotnet/6.0"\u003eDownload from Microsoft\u003c/a\u003e\u003c/p\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003eðŸ’¡ \u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eNote : At the time of compiling this documentation, the latest version available was dotnet-sdk-6.0.100-rc.2.21505.57 \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003ch2 id="ide"\u003eIDE\u003c/h2\u003e\n\u003cp\u003eVisual Studio Code IDE is the recommended IDE to use for fullstackhero\u0026rsquo;s .NET WebApi Boilerplate Project Development. If you are not already using this IDE, consider switching to it. It\u0026rsquo;s definitely worth it! (\u003cem\u003eFun Fact : I recently switched from Visual Studio 2019 Community to Visual Studio Code and it\u0026rsquo;s been awesome!\u003c/em\u003e)\u003c/p\u003e\n\u003cp\u003eHowever, you are always free to use your choice of IDEs as well.\u003c/p\u003e\n\u003cp\u003eIncase you intend to use Visual Studio Code for development, here are a bunch of helpful extensions that I use:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eEditorConfig for VS Code\u003c/li\u003e\n\u003cli\u003eC#\u003c/li\u003e\n\u003cli\u003eC# Extensions\u003c/li\u003e\n\u003cli\u003eDocker\u003c/li\u003e\n\u003cli\u003eMarkdown All in One\u003c/li\u003e\n\u003cli\u003eNuGet Gallery\u003c/li\u003e\n\u003cli\u003eMaterial Icon Theme\u003c/li\u003e\n\u003cli\u003eREST Client\u003c/li\u003e\n\u003c/ul\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003eðŸ’¡ \u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eNote : All the screenshots included in these documentations are from Visual Studio Code Point of View. \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003ch2 id="database-servers"\u003eDatabase Servers\u003c/h2\u003e\n\u003cp\u003efullstackhero\u0026rsquo;s .NET WebApi Boilerplate gives you the freedom to choose between the following 3 popular Database Providers. Please note that with the current architecture of the API Project, it would rather be easy to add in support for more DB Providers with minimal change of code. But as of now, here are the 3 Supported Database Providers! By default, MSSQL is chosen as the Database Provider.\u003c/p\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e\u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eMake sure that you have at least one of these servers installed, along with a Database Management tool like Azure Data Studio / PostgreSQL pgAdmin / MySQL Workbench  \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003ch3 id="mssql"\u003eMSSQL\u003c/h3\u003e\n\u003cp\u003eThere are high chances that you already have this installed on your machine. This is ideal for development and production for small-mid server applications.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eCheck out the Community versions of this Server - \u003ca href="https://www.microsoft.com/en-in/sql-server/sql-server-downloads"\u003eGet from Microsoft\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eDownload SQL Server Management Studio (SSMS) - \u003ca href="https://docs.microsoft.com/en-us/sql/ssms/download-sql-server-management-studio-ssms?view=sql-server-ver15"\u003eGet from Microsoft\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eDownload Azure Data Studio to browse your MSSQL Databases - \u003ca href="https://docs.microsoft.com/en-us/sql/azure-data-studio/download-azure-data-studio"\u003eGet from Microsoft\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id="mysql"\u003eMySQL\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eCheck out the Community versions of this Server - \u003ca href="https://dev.mysql.com/downloads/mysql/"\u003eGet from mysql\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eDownload MySQL Workbench - \u003ca href="https://dev.mysql.com/downloads/workbench/"\u003eGet from mysql\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id="postgresql"\u003ePostgreSQL\u003c/h3\u003e\n\u003cp\u003eProbably the best Open Source Database Server with lots of Enterprise level features.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eDownload postgresql Installer - \u003ca href="https://www.postgresql.org/download/"\u003eGet from postgresql.org\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="api-testing"\u003eAPI Testing\u003c/h2\u003e\n\u003cp\u003eWhen it comes to API Testing, Postman is the recommended tool. I have made sure to include a Postman Collection within the Repository under \u003cstrong\u003e/postman\u003c/strong\u003e folder to make sure you can test out all the existing endpoints. Note that there will be a detailed guide on how to use the given Postman Collection.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eDownload Postman - \u003ca href="https://www.postman.com/downloads/"\u003eGet from postman.com\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="community-tools"\u003eCommunity Tools\u003c/h2\u003e\n\u003ch2 id="optionals"\u003eOptionals\u003c/h2\u003e\n\u003ch3 id="docker"\u003eDocker\u003c/h3\u003e\n'}).add({id:2,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/",title:"Getting Started",description:"Let's get started with the .NET WebApi Boilerplate!",content:'\u003cp\u003eTo start using fullstackhero .NET WebAPI Boilerplate, there are a couple of options.\u003c/p\u003e\n\u003cp\u003eBut before that, make sure that you have already setup your development environment that runs the prerequisite tools and SDKs. Refer \u003ca href="/dotnet-webapi-boilerplate/general/development-environment/"\u003eDevelopment Environment\u003c/a\u003e for details.\u003c/p\u003e\n\u003cp\u003eThis guide will take you right from installing the \u003ccode\u003efullstackhero .NET WebAPI Boilerplate\u003c/code\u003e package to testing the API using the provided Postman Collection!\u003c/p\u003e\n\u003ch2 id="installing-nuget-package"\u003eInstalling NuGet Package\u003c/h2\u003e\n\u003cp\u003eThis is by far the easiest and the most streamlined way of getting the latest available release version of fullstackhero .NET WebApi Boilerplate.\u003c/p\u003e\n\u003cp\u003eOpen up your Command Prompt / Powershell and run the following command to install the solution template.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003edotnet new --install FullStackHero.WebAPI.Boilerplate::0.0.5-rc\n\u003c/code\u003e\u003c/pre\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_500x0_resize_q100_lanczos.jpg 500w" width="1113" height="622" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero_hu7c6c28e87a40d427635729a75465e448_73903_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/general/getting-started/install-fullstackhero.jpg"\n      width="1113" height="622" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003cp\u003eThis would install the \u003ccode\u003efullstackhero .NET WebAPI Boilerplate\u003c/code\u003e template globally on your machine. Do note that, at the time of writing this documentation, the latest available version is \u003cstrong\u003e0.0.5-rc\u003c/strong\u003e which is also one of the first pre-release version of the package. It is highly likely that there is already a newer version available when you are reading this.\u003c/p\u003e\n\u003cp\u003e\u003cem\u003eTo get the latest version of the package, visit \u003ca href="https://www.nuget.org/packages/FullStackHero.WebAPI.Boilerplate"\u003enuget.org\u003c/a\u003e\u003c/em\u003e\u003c/p\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e\u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eFullStackHero.WebAPI.Boilerplate is now in pre-release state. You can find the latest version on NuGet.org \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003ch2 id="creating-your-first-solution"\u003eCreating your First Solution\u003c/h2\u003e\n\u003cp\u003eNow that you have installed the template locally on your machine, let\u0026rsquo;s see how you can start generating complete .NET WebAPI Solutions seamlessly.\u003c/p\u003e\n\u003cp\u003eSimply navigate to a new directory (wherever you want to place your new solution at), and open up Command Prompt at the opened directory.\u003c/p\u003e\n\u003cp\u003eRun the following command. Note that, in this demonstration I am naming my new solution as \u003ccode\u003eFSH.Starter\u003c/code\u003e.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003edotnet new fsh-api -o FSH.Starter\n\u003c/code\u003e\u003c/pre\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_500x0_resize_q100_lanczos.jpg 500w" width="1113" height="622" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/generate-solution_hud872c74a6a8cebad2cfe2304dd865871_46151_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/general/getting-started/generate-solution.jpg"\n      width="1113" height="622" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003cp\u003eOnce that is done, your new solution is created for you. As simple as that!\u003c/p\u003e\n\u003cp\u003eHere are the folders and files created for you.\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_500x0_resize_q100_lanczos.jpg 500w" width="649" height="431" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/folder-structure_hufad432d56a96f488860d5d823c4cdcd6_118918_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/general/getting-started/folder-structure.jpg"\n      width="649" height="431" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003ch2 id="alternatively"\u003eAlternatively..\u003c/h2\u003e\n\u003cp\u003eWhen you installed the NuGet package, there is also an entry that has been created into your Visual Studio Template for fullstackhero\u0026rsquo;s .NET WebAPI Boilerplate. If you find it easier to work with Visual Studio rather than CLI Commands to generate new solutions, you are free to do so.\u003c/p\u003e\n\u003cp\u003eSimply open up Visual Studio 2022 and Click on Create New Project.\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_500x0_resize_q100_lanczos.jpg 500w" width="1012" height="674" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support_hu00bef45250d365283da6e25785f6e784_104503_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/general/getting-started/visual-studio-support.jpg"\n      width="1012" height="674" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003cp\u003eImportant - Make sure to check the \u0026lsquo;Place solution and project in same directory\u0026rsquo; option. Else the solution and projects will be created on different folders and there will be build errors stating that few files are not found.\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_500x0_resize_q100_lanczos.jpg 500w" width="1185" height="391" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory_hu54afb7ed2573a40d4d367c84911f7d85_24014_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/general/getting-started/vs-same-directory.jpg"\n      width="1185" height="391" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003cp\u003eAnother issue I noticed with creating solutions via Visual Studio is that the Solution structure might be lost. This is a very minor bug, that maybe someone can figure out and fix in our template configuration. Microsoft doesn\u0026rsquo;t seem to have very detailed guide about this.\u003c/p\u003e\n\u003cp\u003eHowever, it\u0026rsquo;s always recommended to create new solutions via the Console.\u003c/p\u003e\n\u003ch2 id="running-the-application"\u003eRunning the Application\u003c/h2\u003e\n\u003cp\u003eNext, open up command prompt on this directory and run the following.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003ecode .\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eThis opens up the solution via Visual Code. Make sure that you have the prerequisite tools and SDKs setup.\u003c/p\u003e\n\u003ch3 id="setting-up-the-connection-string"\u003eSetting up the Connection String\u003c/h3\u003e\n\u003cp\u003eNext, let\u0026rsquo;s set up some valid connection strings. Navigate to \u003ccode\u003esrc/Host/Configurations\u003c/code\u003e and open up \u003ccode\u003edatabase.json\u003c/code\u003e. Here you would have to provide a valid connection string under the \u003ccode\u003eDatabaseSettings\u003c/code\u003e to either MSSQL,  MySQL or PostgreSQL instance. Below are some sample settings for each of the DB Providers.\u003c/p\u003e\n\u003cp\u003eDetails on the usage of other Settings will be explained in the upcoming documentations.\u003c/p\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e\u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eIt is important to update the src/Host/Configurations/hangfire.json connection string / provider as well. \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003ch4 id="mysql"\u003eMySQL\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e\u0026quot;DatabaseSettings\u0026quot;: {\n    \u0026quot;ConnectionString\u0026quot;: \u0026quot;server=localhost;uid=root;pwd=root;database=defaultRootDb;Allow User Variables=True\u0026quot;,\n    \u0026quot;DBProvider\u0026quot;: \u0026quot;mysql\u0026quot;\n  }\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="mssql"\u003eMSSQL\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e\u0026quot;DatabaseSettings\u0026quot;: {\n    \u0026quot;DBProvider\u0026quot;: \u0026quot;mssql\u0026quot;,\n    \u0026quot;ConnectionString\u0026quot;: \u0026quot;Data Source=(localdb)\\\\mssqllocaldb;Initial Catalog=rootTenantDb;Integrated Security=True;MultipleActiveResultSets=True\u0026quot;\n  }\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="postgresql"\u003ePostgreSQL\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e\u0026quot;DatabaseSettings\u0026quot;: {\n    \u0026quot;ConnectionString\u0026quot;: \u0026quot;Host=localhost;Database=rootTenantDb;Username=postgres;Password=root;Include Error Detail=true\u0026quot;,\n    \u0026quot;DBProvider\u0026quot;: \u0026quot;postgresql\u0026quot;\n  }\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="oracle"\u003eOracle\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e{\n  \u0026quot;DatabaseSettings\u0026quot;: {\n    \u0026quot;DBProvider\u0026quot;: \u0026quot;oracle\u0026quot;,\n    \u0026quot;ConnectionString\u0026quot;: \u0026quot;Data Source=(DESCRIPTION=(ADDRESS=(PROTOCOL=TCP)(HOST=127.0.0.1)(PORT=49154))(CONNECT_DATA =(SERVER=DEDICATED)(SERVICE_NAME=ORCLPDB1.localdomain)));User Id=fullstack;Password=password123\u0026quot;\n  }\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eNow you need to navigate to the \u003ccode\u003eHost (API) Project\u003c/code\u003e directory via CMD or VSCode\u0026rsquo;s native terminal and run the basic build and run command to get the API up and running. Run the following.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e cd src/Host\n dotnet build\n dotnet run\n\u003c/code\u003e\u003c/pre\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_500x0_resize_q100_lanczos.jpg 500w" width="1113" height="622" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/getting-started/running-api_hu89cdd8c0a7ee9cdcc1ff2ac5bd6a63d3_310023_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/general/getting-started/running-api.jpg"\n      width="1113" height="622" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003cp\u003eAs you can see from the logs, a couple of operations happen as soon as you launch the application. Let me give a brief idea on what happens when you run the application for the very first time.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eThe Migrations that already come out-of-the-box with the application gets applied. Note that you do not have to update the database.\u003c/li\u003e\n\u003cli\u003eThe Application is programmed to seed a default Tenant named \u003ccode\u003eroot\u003c/code\u003e, that is basically the super-admin of the entire application and has permissions to manage tenants.\u003c/li\u003e\n\u003cli\u003eOnce Tenant record is seeded, the tenant admin , roles and permissions are also seeded. Note that the default credentials for the root tenant admin is as follows.\u003c/li\u003e\n\u003c/ul\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e{\n    \u0026quot;email\u0026quot;:\u0026quot;admin@root.com\u0026quot;,\n    \u0026quot;password\u0026quot;:\u0026quot;123Pa$$word!\u0026quot;\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cul\u003e\n\u003cli\u003eThe Connection String that you provided in the appSettings will be taken as the \u003ccode\u003eroot\u003c/code\u003e Tenant\u0026rsquo;s Connection. Note that all the tenant data will be stored on to this connection under the Tenants table.\u003c/li\u003e\n\u003c/ul\u003e\n'}).add({id:3,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/",title:"API Testing",description:"Testing fullstackhero's Web API",content:'\u003cp\u003efullstackhero\u0026rsquo;s Web API can be tested via Postman as well as the built in Swagger.\u003c/p\u003e\n\u003ch2 id="postman"\u003ePostman\u003c/h2\u003e\n\u003cp\u003eNow that our application is up and running, let\u0026rsquo;s fire up POSTMAN and run some basic requests. You can find the updated Postman collection under the /postman folder of your solution. Import this postman collection on to your local Postman.\u003c/p\u003e\n\u003cp\u003eNote that I have set up some default collection variables to make things easier.\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_500x0_resize_q100_lanczos.jpg 500w" width="1126" height="344" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/postman-collection_hudb63cdb1b52ee885abdbc88f65024878_78993_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/general/api-testing/postman-collection.jpg"\n      width="1126" height="344" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003cp\u003eIn case your application happens to start on a different Port, you would have to change the \u003ccode\u003eurl\u003c/code\u003e variable on this collection and save it.\u003c/p\u003e\n\u003ch3 id="generating-access-token"\u003eGenerating Access Token\u003c/h3\u003e\n\u003cp\u003eLet\u0026rsquo;s start by generating a token for the \u003ccode\u003eroot admin\u003c/code\u003e user! Navigate to the Identity/get-token request and simply run it. If everything goes as expected, you would be seeing a valid token as your response.\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_500x0_resize_q100_lanczos.jpg 500w" width="1780" height="677" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/general/api-testing/token-response_huca89a35b58fa90c27d4272e37f0b4eec_273910_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/general/api-testing/token-response.jpg"\n      width="1780" height="677" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003ch3 id="search-brands"\u003eSearch Brands\u003c/h3\u003e\n\u003cp\u003eWhenever a valid token is generated via Postman, it is stored as a variable of the collection and is used by default for all the subsequent requests via Postman until the token expires. You can test that the token is valid by sending the \u0026lsquo;search-brands\u0026rsquo; request.\u003c/p\u003e\n\u003cp\u003eYour request body would look somewhat like this:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e{\n  \u0026quot;keyword\u0026quot;: \u0026quot;\u0026quot;,\n  \u0026quot;pageNumber\u0026quot;: 0,\n  \u0026quot;pageSize\u0026quot;: 10,\n  \u0026quot;orderBy\u0026quot;: [\n    \u0026quot;id\u0026quot;\n  ]\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eSo, by default as soon as your application in run for the first time, a couple of Brands are seeded into the database for demo purposes.\u003c/p\u003e\n\u003cp\u003eThe \u003cem\u003ekeyword\u003c/em\u003e parameter in the above request denotes the keyword you want to search for in the brands table. Let\u0026rsquo;s leave it empty for now and let all the other parameters stay unchanged.\u003c/p\u003e\n\u003cp\u003ePresuming that you have a valid token you\u0026rsquo;ll see:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e{\n    \u0026quot;data\u0026quot;: [\n        {\n            \u0026quot;id\u0026quot;: \u0026quot;623e0000-3f5a-3c7c-c326-08d9b2a1c527\u0026quot;,\n            \u0026quot;name\u0026quot;: \u0026quot;Razor\u0026quot;,\n            \u0026quot;description\u0026quot;: \u0026quot;Sample Data\u0026quot;\n        },\n        {\n            \u0026quot;id\u0026quot;: \u0026quot;623e0000-3f5a-3c7c-dce4-08d9b2a1c527\u0026quot;,\n            \u0026quot;name\u0026quot;: \u0026quot;Samsung\u0026quot;,\n            \u0026quot;description\u0026quot;: \u0026quot;Sample Data\u0026quot;\n        },\n        {\n            \u0026quot;id\u0026quot;: \u0026quot;623e0000-3f5a-3c7c-dd82-08d9b2a1c527\u0026quot;,\n            \u0026quot;name\u0026quot;: \u0026quot;MSI\u0026quot;,\n            \u0026quot;description\u0026quot;: \u0026quot;Sample Data\u0026quot;\n        },\n        {\n            \u0026quot;id\u0026quot;: \u0026quot;623e0000-3f5a-3c7c-ddb0-08d9b2a1c527\u0026quot;,\n            \u0026quot;name\u0026quot;: \u0026quot;Huawei\u0026quot;,\n            \u0026quot;description\u0026quot;: \u0026quot;Sample Data\u0026quot;\n        }\n    ],\n    \u0026quot;currentPage\u0026quot;: 1,\n    \u0026quot;totalPages\u0026quot;: 1,\n    \u0026quot;totalCount\u0026quot;: 4,\n    \u0026quot;pageSize\u0026quot;: 10,\n    \u0026quot;hasPreviousPage\u0026quot;: false,\n    \u0026quot;hasNextPage\u0026quot;: false,\n    \u0026quot;messages\u0026quot;: null,\n    \u0026quot;succeeded\u0026quot;: true\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eThere you go.\u003c/p\u003e\n\u003ch3 id="creating-a-new-brand"\u003eCreating a New Brand\u003c/h3\u003e\n\u003cp\u003eOpen the \u003cstrong\u003ecreate-brands\u003c/strong\u003e request in Postman and run it. Click the \u0026lsquo;Body\u0026rsquo; tab to review the values we\u0026rsquo;ll be sending. After sending you should see a something like:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e{\n    \u0026quot;data\u0026quot;: \u0026quot;0f8e6d6e-d574-4d2f-b9ac-4dd1c9ffa0ae\u0026quot;,\n    \u0026quot;messages\u0026quot;: [],\n    \u0026quot;succeeded\u0026quot;: true\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eResend the \u003cstrong\u003e\u0026lsquo;search-brands\u0026rsquo;\u003c/strong\u003e request to see your new brand listed in the response!\u003c/p\u003e\n\u003ch3 id="create-a-new-product"\u003eCreate a new Product\u003c/h3\u003e\n\u003cp\u003eNext let\u0026rsquo;s add a product. Run the \u003cstrong\u003e\u0026lsquo;create-product\u0026rsquo;\u003c/strong\u003e request at this point and you\u0026rsquo;ll see a failure message:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003e{\n    \u0026quot;source\u0026quot;: \u0026quot;DN.WebApi.Application\u0026quot;,\n    \u0026quot;exception\u0026quot;: \u0026quot;brand.notfound [en-US]\u0026quot;,\n    \u0026quot;errorCode\u0026quot;: 404,\n    \u0026quot;succeeded\u0026quot;: false\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eWhat went wrong?? Click the \u0026lsquo;\u003cstrong\u003eBody\u003c/strong\u003e\u0026rsquo; tab and look at the value for the \u003cem\u003ebrandId\u003c/em\u003e. That field needs to match the \u003cem\u003eID\u003c/em\u003e value of the \u003cstrong\u003eBrands\u003c/strong\u003e table. When I created the Postman collection I had to use the values that existed while I was building the project from my workstation. If you revisit the \u0026lsquo;\u003cstrong\u003esearch-brands\u003c/strong\u003e\u0026rsquo; request and look at the results you\u0026rsquo;ll see a value for \u0026lsquo;\u003cem\u003eid\u003c/em\u003e\u0026rsquo;. Copy that value - return to the `\u003cstrong\u003ecreate-product\u003c/strong\u003e\' request - replace the id value with what you\u0026rsquo;d copied and re-send the command. It should report success now.\u003c/p\u003e\n'}).add({id:4,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/general/cli-commands/",title:"CLI Commands",description:"Here are some important CLI Commands that are to be used along with fullstackhero.",content:'\u003ch2 id="docker"\u003eDocker\u003c/h2\u003e\n\u003ch3 id="build"\u003eBuild\u003c/h3\u003e\n\u003cp\u003eThis command is to be executed from the root folder of the project.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003edocker build -t iammukeshm/dotnet-webapi:0.0.3-rc -t iammukeshm/dotnet-webapi:latest  .\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="push-to-dockerhub"\u003ePush to DockerHub\u003c/h3\u003e\n\u003cp\u003eThis command is to be executed from the root folder of the project.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003edocker push iammukeshm/dotnet-webapi\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch2 id="migrations"\u003eMigrations\u003c/h2\u003e\n\u003cp\u003eThis command is to be executed from the Host Directory of the project.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003edotnet ef migrations add \u0026lt;CommitMessage\u0026gt; --project .././Migrators/Migrators.\u0026lt;Provider\u0026gt;/ --context ApplicationDbContext -o Migrations/Application\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eCommitMessage : Enter a commit message here.\nProvider : Enter the available DB Provider name. MSSQL , MySQL , PostgreSQL , Oracle\u003c/p\u003e\n\u003cp\u003eWhile adding migrations for a particular provider, ensure that you have configured a valid connection string to the provider\u0026rsquo;s database at both \u003ccode\u003esrc/Host/Configurations/database.json\u003c/code\u003e and \u003ccode\u003esrc/Host/Configurations/hangfire.json\u003c/code\u003e.\u003c/p\u003e\n\u003ch2 id="nuget"\u003eNuGet\u003c/h2\u003e\n\u003cp\u003eGenerates a NuGet Package of the entire solution. NuGet Configuration is available in the *.nuspec file at the root of the project directory. This command is to be executed from the Root Directory of the project.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-powershell"\u003enuget pack -NoDefaultExcludes\n\u003c/code\u003e\u003c/pre\u003e\n'}).add({id:5,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/general/project-structure/",title:"Project Structure",description:"Here is how the .NET WebApi Boilerplate is structured.",content:'\u003cp\u003efullstackhero\u0026rsquo;s .NET WebApi Boilerplate is based on Clean Architecture. In other words, Onion / Hexagonal Architecture. \u003ca href="%22https://codewithmukesh.com/blog/onion-architecture-in-aspnet-core/%22"\u003eRead about the advantages and principles of Onion Architecture here â†’\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id="general-structure"\u003eGeneral Structure\u003c/h2\u003e\n\u003cp\u003eThis means that the entire solution is built in such a way that it can be scaled, maintained easily by teams of developers. This WebAPI Solution Primarily consists of the following .csproj files.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003eâ”œâ”€â”€ src\nâ”‚   â”œâ”€â”€ Host.csproj\nâ”‚   â”œâ”€â”€ Core\nâ”‚   |   â”œâ”€â”€ Application.csproj\nâ”‚   |   â””â”€â”€ Domain.csproj\n|   â”œâ”€â”€ Infrastructure.csproj\n|   â”œâ”€â”€ Migrators\nâ”‚   |   â”œâ”€â”€ Migrators.MSSQL.csproj\nâ”‚   |   â”œâ”€â”€ Migrators.MySQL.csproj\nâ”‚   |   â”œâ”€â”€ Migrators.PostgreSQL.csproj\nâ”‚   |   â””â”€â”€ Migrators.Oracle.csproj\nâ”‚   â””â”€â”€ Shared\nâ”‚       â””â”€â”€ Shared.DTOs.csproj\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eThe idea is to build a very loosely coupled architecture following best practices and packages. Let\u0026rsquo;s see in brief what responsibilities each of these projects handle.\u003c/p\u003e\n\u003ch3 id="host"\u003eHost\u003c/h3\u003e\n\u003cp\u003eContains the API Controllers and Startup Logic including ASP.NET Core Container setup. This is the entry point of the application. Also, other static files like the logs, localization jsons, images, email templates and most importantly the appsettings.json live under this project.\u003c/p\u003e\n\u003cp\u003eWith 0.0.5-rc, the appSettings.json is further split into variable sub-setting like database.json, security.json and so on for better modularity and organization. You can find these new JSONs under the Configurations folder of the Host project.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003eâ”œâ”€â”€ Host\n|   â”œâ”€â”€ Configurations\n|   â”œâ”€â”€ Controllers\n|   â”œâ”€â”€ Email Templates\n|   â”œâ”€â”€ Extensions\n|   â”œâ”€â”€ Files\nâ”‚   |   â”œâ”€â”€ Images\nâ”‚   |   â””â”€â”€ Documents\n|   â”œâ”€â”€ Localization\n|   â”œâ”€â”€ Logs\n|   â””â”€â”€ appsettings.json\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eNote that the \u003cem\u003eHost\u003c/em\u003e project depends on\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eApplication\u003c/li\u003e\n\u003cli\u003eInfrastructure\u003c/li\u003e\n\u003cli\u003eMigration Projects\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id="application"\u003eApplication\u003c/h3\u003e\n\u003cp\u003eThis is one of the projects in the Core Folder apart from the Domain Project. Here you get to see Abstract Classes and Interfaces that are inherited and implemented in the Infrastructure Project. This refers to Dependency Inversion.\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003eâ”œâ”€â”€ Core\n|   â”œâ”€â”€ Application\n|   |   â”œâ”€â”€ Auditing\n|   |   â”œâ”€â”€ Catalog\n|   |   â”œâ”€â”€ Common\n|   |   â”œâ”€â”€ Dashboard\n|   |   â”œâ”€â”€ DependencyInjection\n|   |   â”œâ”€â”€ Identity\n|   |   â”œâ”€â”€ Multitenancy\n|   |   â”œâ”€â”€ Settings\n|   |   â”œâ”€â”€ Storage\n|   |   â””â”€â”€ Wrapper\n\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eThe folders and split at the top level Feature-wise. Meaning, it now makes it easier for developers to understand the folder structure. Each of the feature folders like Catalog will have further sub-folders like\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eEventHandlers\u003c/li\u003e\n\u003cli\u003eInterfaces\u003c/li\u003e\n\u003cli\u003eServices\u003c/li\u003e\n\u003cli\u003eValidators\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eand so on. Thus everything related to a feature will be found directly under that Feature folder.\u003c/p\u003e\n\u003cp\u003eIn cases where there are less number of classes / interfaces associated with a feature, all of these classes are put directly under the root of the feature folder. Only when the complexity of the feature increases, it is recommended to separate the classes by their type.\u003c/p\u003e\n\u003cp\u003eNote that the \u003cem\u003eApplication\u003c/em\u003e project does not depend on any other project.\u003c/p\u003e\n\u003ch3 id="domain"\u003eDomain\u003c/h3\u003e\n\u003cp\u003eNote that the \u003cem\u003eDomain\u003c/em\u003e project does not depend on any other project.\u003c/p\u003e\n\u003cp\u003eAs per Clean Architecture principles, the Core of this Solution i.e, Application and Domain projects do not depend on any other projects. This helps achieve Dependency Inversion (The \u0026lsquo;D\u0026rsquo; Principle of \u0026lsquo;SOLID\u0026rsquo;).\u003c/p\u003e\n'}).add({id:6,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/general/changelogs/",title:"Changelogs",description:"Track fullstackhero's Web API Changelogs.",content:'\u003ch3 id="005-rc"\u003e0.0.5-rc\u003c/h3\u003e\n\u003cp\u003e\u003ccode\u003eCurrent Stable Version\u003c/code\u003e \u003ccode\u003e28/11/2021\u003c/code\u003e\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eFixed Password Reset / Forgot\u003c/li\u003e\n\u003cli\u003eFixed Omnisharp Warnings / Code Cleanup\u003c/li\u003e\n\u003cli\u003eFixed Repository Method\u003c/li\u003e\n\u003cli\u003eLog Hangfire to Serilog - Thanks \u003ca href="https://github.com/frankyjquintero"\u003efrankyjquintero\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eExtras Hangfire extensions - Thanks \u003ca href="https://github.com/frankyjquintero"\u003efrankyjquintero\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eHangfire implementation examples - Thanks \u003ca href="https://github.com/frankyjquintero"\u003efrankyjquintero\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eSeparate JSON files for each Settings - Thanks \u003ca href="https://github.com/unchase"\u003eunchase\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eAdded Events and Handlers via MediatR - Thanks \u003ca href="https://github.com/frankyjquintero"\u003efrankyjquintero\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eMoved to Tenancy Middleware - Thanks \u003ca href="https://github.com/frankyjquintero"\u003efrankyjquintero\u003c/a\u003e \u0026amp; \u003ca href="https://github.com/fretje"\u003efretje\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eSolution Cleanup - rulesets \u0026amp; analyzer - Thanks \u003ca href="https://github.com/fretje"\u003efretje\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eAdded More Functions for RepositoryAsync - Thanks \u003ca href="https://github.com/ghaithprosoft"\u003eghaithprosoft\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eEntity Database Seeding Simplified\u003c/li\u003e\n\u003cli\u003eFile-Scoped namespaces and implicit usings\u003c/li\u003e\n\u003cli\u003eBetter Folder Structure / Modular\u003c/li\u003e\n\u003cli\u003eMinimal API\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id="004-rc"\u003e0.0.4-rc\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eBug fix in Repository / Update method.\u003c/li\u003e\n\u003cli\u003eEndpoint changes for Update / Delete Methods. (Postman Collection Updated)\u003c/li\u003e\n\u003cli\u003eFixed bug in Request Logging Middleware.\u003c/li\u003e\n\u003cli\u003eDisabled Request Logger by default to save log space.\u003c/li\u003e\n\u003cli\u003eAdds default Role (Basic) to any new registered user.\u003c/li\u003e\n\u003cli\u003eBasic User has default View / Search Permissions\u003c/li\u003e\n\u003cli\u003eCode Cleanups\u003c/li\u003e\n\u003cli\u003eTemplated Emails for User Registrations\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id="003-rc"\u003e0.0.3-rc\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eSecurity Patches\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id="002-rc"\u003e0.0.2-rc\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eMigration Refresh\u003c/li\u003e\n\u003cli\u003eSecurity Patches\u003c/li\u003e\n\u003cli\u003eImage Upload Fix\u003c/li\u003e\n\u003cli\u003eOption to Disable Swagger\u003c/li\u003e\n\u003cli\u003eCleaner Code\u003c/li\u003e\n\u003cli\u003eActivate / Deactivate Tenants\u003c/li\u003e\n\u003cli\u003eUpgrade Tenant Subscription\u003c/li\u003e\n\u003cli\u003eAdvanced Search\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id="001-rc"\u003e0.0.1-rc\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eInitial release\u003c/li\u003e\n\u003c/ul\u003e\n'}).add({id:7,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/configurations/",title:"Configurations",description:"Understanding Configurations in fullstackhero's Web API.",content:'\u003cp\u003eWithin the Host boot project there is a folder called \u0026ldquo;Configurations\u0026rdquo;. where there are all the configuration files, one for each area.\u003c/p\u003e\n\u003ch2 id="general-structure"\u003eGeneral Structure\u003c/h2\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003eâ”œâ”€â”€ Host.csproj\nâ”‚   â”œâ”€â”€ Configurations\nâ”‚   |   â”œâ”€â”€ cache.json\nâ”‚   |   â”œâ”€â”€ cors.json\nâ”‚   |   â”œâ”€â”€ database.json\nâ”‚   |   â”œâ”€â”€ hangfire.json\nâ”‚   |   â”œâ”€â”€ logger.json\nâ”‚   |   â”œâ”€â”€ mail.json\nâ”‚   |   â”œâ”€â”€ middleware.json\nâ”‚   |   â”œâ”€â”€ security.json\nâ”‚   |   â””â”€â”€ signalr.json\n|   â”œâ”€â”€ appsettings.json\n|\n\u003c/code\u003e\u003c/pre\u003e\n\u003cblockquote\u003e\n\u003cp\u003eThe classic appsettings.json configuration file is still available to manage custom parameters.\u003c/p\u003e\n\u003c/blockquote\u003e\n\u003cp\u003eThe \u003cstrong\u003e\u003ccode\u003eStartup\u003c/code\u003e class\u003c/strong\u003e inside the folder is responsible for loading all the configuration files described above.\u003c/p\u003e\n'}).add({id:8,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/exception-handling/",title:"Exception Handling",description:"Understanding Exception Handling in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:9,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/localization/",title:"Localization",description:"Understanding Localization in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:10,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/application-startup/",title:"Application Startup",description:"Understanding application-startup in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:11,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/caching/",title:"Caching",description:"Understanding Caching in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:12,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/cors/",title:"CORS",description:"Understanding CORS in fullstackhero's Web API.",content:"\u003cp\u003eCORS is an mechanism that allows a server to indicate any origins (domain, scheme, or port) other than its own.\u003c/p\u003e\n\u003cp\u003eFullStackHero Web API has support for providing to several clients, all of the configurations related this feature can be found under \u003ccode\u003esrc/Host/Configurations/cors.json\u003c/code\u003e.\u003c/p\u003e\n\u003cp\u003eWe present two scenarios that demonstrate how Cross-Origin requests by clients can be served:\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003e{\n  \u0026quot;CorsSettings\u0026quot;: {\n    \u0026quot;Angular\u0026quot;: \u0026quot;http://localhost:4200\u0026quot;,\n    \u0026quot;Blazor\u0026quot;: \u0026quot;https://localhost:5002;https://www.mydomain.my\u0026quot;\n  }\n}\n\u003c/code\u003e\u003c/pre\u003e\n"}).add({id:13,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/hangfire/",title:"Hangfire",description:"Understanding Hangfire in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:14,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/",title:"Logging",description:"Understanding Logging in fullstackhero's Web API.",content:'\u003cp\u003eFullStackHero Web API has out of the box support for logging to several destinations. Thanks to the usage of Serilog! All of the configurations related to Logging can be found under \u003ccode\u003esrc/Host/Configurations/logger.json\u003c/code\u003e.\u003c/p\u003e\n\u003ch2 id="console"\u003eConsole\u003c/h2\u003e\n\u003cp\u003eConsole Logging is enabled by Default. This is done directly in the C# code and not in the JSON Configurations. You can find the code snippet at the Host\u0026rsquo;s Program.cs. Package used - \u003ccode\u003eSerilog.Sinks.Console\u003c/code\u003e\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003ebuilder.Host.UseSerilog((_, config) =\u0026gt;\n{\n    config.WriteTo.Console()\n    .ReadFrom.Configuration(builder.Configuration);\n});\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eHere is how the logs show up on Console via Serilog.\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_500x0_resize_q100_lanczos.jpg 500w" width="1289" height="258" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/console-logging_huaed0335094867202bfee7a4f844f185f_150217_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/fundamentals/logging/console-logging.jpg"\n      width="1289" height="258" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003ch2 id="file"\u003eFile\u003c/h2\u003e\n\u003cp\u003eStructured logging is the best part of using Serilog! It is possible to use JSON formatters to evenly log messages and warning to files in the local directory. Package used - \u003ccode\u003eSerilog.Sinks.File\u003c/code\u003e\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003e\u0026quot;Name\u0026quot;: \u0026quot;File\u0026quot;,\n\u0026quot;Args\u0026quot;: {\n  \u0026quot;path\u0026quot;: \u0026quot;Logs/logs.json\u0026quot;,\n  \u0026quot;formatter\u0026quot;: \u0026quot;Serilog.Formatting.Json.JsonFormatter, Serilog\u0026quot;,\n  \u0026quot;rollingInterval\u0026quot;: \u0026quot;Day\u0026quot;,\n  \u0026quot;restrictedToMinimumLevel\u0026quot;: \u0026quot;Information\u0026quot;,\n  \u0026quot;retainedFileCountLimit\u0026quot;: 5\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eThe above configurations ensures that the logs are structured and has a new filename every day. Since we do not want to use of a lot of memory to keep these logs, it\u0026rsquo;s configured to keep the logs of the last 5 days only. Feel free to modify this. Also this only logs the messages that has a level of Information or above.\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_500x0_resize_q100_lanczos.jpg 500w" width="1848" height="400" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/file-logging_hu88e908a4011519b4b9f9e14a9c8f1712_198841_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/fundamentals/logging/file-logging.jpg"\n      width="1848" height="400" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003ch2 id="seq"\u003eSEQ\u003c/h2\u003e\n\u003cp\u003eHaving a dashboard to analyze the logs and warning are always good to have. SEQ is one option for that. It collects application logs, filters it as you need, along with several other integrations.\u003c/p\u003e\n\u003cp\u003ePackage used - \u003ccode\u003eSerilog.Sinks.Seq\u003c/code\u003e\u003c/p\u003e\n\u003cp\u003eMake sure you have SEQ installed and running on your machine / environment. SEQ is free for Individual use. Once setup, point to the SEQ Instance. It usually runs over the \u003ccode\u003e5341\u003c/code\u003e port.\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003e\u0026quot;Name\u0026quot;: \u0026quot;Seq\u0026quot;,\n\u0026quot;Args\u0026quot;: {\n  \u0026quot;serverUrl\u0026quot;: \u0026quot;http://localhost:5341\u0026quot;\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_500x0_resize_q100_lanczos.jpg 500w" width="1920" height="961" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging_hu60b4b70a48b92f81cb13e4372c04079b_635300_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/fundamentals/logging/seq-logging.jpg"\n      width="1920" height="961" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003ch2 id="elastic-search"\u003eElastic Search\u003c/h2\u003e\n\u003cp\u003eNow, the interesting part! Elastic Search with Kibana for Logging. You will need instances of Kibana and ElasticSearch running. To make things simpler, I have included a docker-compose \u003ccode\u003edeployments\\elk\\docker-compose.elk.yml\u003c/code\u003e which has references to both Kibana and ElasticSearch Images of version \u003ccode\u003e7.16.0\u003c/code\u003e.\u003c/p\u003e\n\u003cp\u003eEnsure that you have Docker installed on your machine along with the Docker Extensions for Visual Code (or your favorite IDE). From VS Code, you can simply right click \u003ccode\u003e\\deployments\\elk\\docker-compose.elk.yml\u003c/code\u003e and click on Docker-Up. This would spin up the Kibana and ES instances for you in no time.\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_500x0_resize_q100_lanczos.jpg 500w" width="1270" height="720" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk_hud9ef020c8227b23a98be4a162e43af44_94452_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/fundamentals/logging/docker-elk.jpg"\n      width="1270" height="720" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n\u003cp\u003eBy default,\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eKiabana runs on port 5601 - http://localhost:5601\u003c/li\u003e\n\u003cli\u003eElastic Search runs on port 9200 - http://localhost:9200\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003ePackage used - \u003ccode\u003eSerilog.Sinks.Elasticsearch\u003c/code\u003e\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003e\u0026quot;Name\u0026quot;: \u0026quot;Elasticsearch\u0026quot;,\n\u0026quot;Args\u0026quot;: {\n  \u0026quot;nodeUris\u0026quot;: \u0026quot;http://localhost:9200;\u0026quot;,\n  \u0026quot;indexFormat\u0026quot;: \u0026quot;DN.WebApi-logs-{0:yyyy.MM}\u0026quot;,\n  \u0026quot;numberOfShards\u0026quot;: 2,\n  \u0026quot;numberOfReplicas\u0026quot;: 1,\n  \u0026quot;restrictedToMinimumLevel\u0026quot;: \u0026quot;Information\u0026quot;\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eYou can see that we are pointing to port 9200 from our configuration. This instructs Serilog to write the logs to ElasticSearch DB.\u003c/p\u003e\n\u003cp\u003eTo view the logs on Dashboard, navigate to http://localhost:5601\u003c/p\u003e\n\u003cp\u003eFirstly, navigate to Kibana Spaces - http://localhost:5601/app/management/kibana/spaces\u003c/p\u003e\n\u003cp\u003eFrom the sidebar, select Index Patterns and create a new one.\u003c/p\u003e\n\u003cp\u003eIn the Name field - put in \u003ccode\u003edn.webapi-logs*\u003c/code\u003e or whatever you have set in your Serilog Configuration under \u003ccode\u003eindexFormat\u003c/code\u003e. Select the Timestamp field as \u003ccode\u003e@timestamp\u003c/code\u003e. That\u0026rsquo;s it.\u003c/p\u003e\n\u003cp\u003eNow, go to http://localhost:5601/app/discover\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg class="doc-img lazyload" src="/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_20x0_resize_q100_lanczos.jpg"\n    data-srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_500x0_resize_q100_lanczos.jpg 500w" width="1920" height="1009" \u003e\n  \u003cnoscript\u003e\u003cimg class="doc-img img-fluid" srcset="https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_900x0_resize_q100_lanczos.jpg 900w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_800x0_resize_q100_lanczos.jpg 800w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_700x0_resize_q100_lanczos.jpg 700w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_600x0_resize_q100_lanczos.jpg 600w,https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/logging/kibana_hub8dd9ffe1854b272dacb2cf1a46ee863_607133_500x0_resize_q100_lanczos.jpg 500w" src="/dotnet-webapi-boilerplate/fundamentals/logging/kibana.jpg"\n      width="1920" height="1009" \u003e\u003c/noscript\u003e\n  \n  \u003c/figure\u003e\n\n'}).add({id:15,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/mail-service/",title:"Mail Service",description:"Understanding Mail Service in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:16,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/middlewares/",title:"Middlewares",description:"Understanding Middlewares in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:17,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/multitenancy/",title:"Multitenancy",description:"Understanding Multitenancy in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:18,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/security/",title:"Security",description:"Understanding Security in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:19,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/signalr/",title:"SignalR",description:"Understanding SignalR in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:20,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/fundamentals/swagger/",title:"Swagger",description:"Understanding Swagger in fullstackhero's Web API.",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:21,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/tutorials/crud-guide/",title:"Performing CRUD Operations",description:"Performing CRUD Operations with fullstackhero's Web API",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:22,href:"https://fullstackhero.net/dotnet-webapi-boilerplate/tutorials/azure-ad-guide/",title:"Setting up Azure AD Authentication",description:"Setting up Azure AD Authentication with fullstackhero's Web API",content:"\u003cp\u003eDocumentation Coming Soon!\u003c/p\u003e\n"}).add({id:23,href:"https://fullstackhero.net/blog/say-hello-to-fullstackhero/",title:"Say hello to fullstackhero ðŸ‘‹",description:".NET WebAPI Boilerplate Template built with .NET 6.0. Incorporates the most essential Packages your projects will ever need. Follows Clean Architecture Principles.",content:""}),userinput.addEventListener('input',b,!0),suggestions.addEventListener('click',c,!0);function b(){var d,e;const c=5;d=this.value,e=a.search(d,{limit:c,enrich:!0}),suggestions.classList.remove('d-none'),suggestions.innerHTML="";const b={};e.forEach(a=>{a.result.forEach(a=>{b[a.doc.href]=a.doc})});for(const d in b){const e=b[d],a=document.createElement('div');if(a.innerHTML='<a href><span></span><span></span></a>',a.querySelector('a').href=d,a.querySelector('span:first-child').textContent=e.title,a.querySelector('span:nth-child(2)').textContent=e.description,suggestions.appendChild(a),suggestions.childElementCount==c)break}}function c(){while(suggestions.lastChild)suggestions.removeChild(suggestions.lastChild);return!1}})()